            <script>
                document.addEventListener('DOMContentLoaded', (event) => {
                    const searchText = document.getElementById('searchTextFullFrame');
                    const inputIconClose = document.querySelector('.input-icon-close');

                    function toggleIconVisibility() {
                        if (searchText.value.trim() === '') {
                            inputIconClose.style.display = 'none';
                        } else {
                            inputIconClose.style.display = 'block';
                        }
                    }

                    // Initial check
                    toggleIconVisibility();

                    // Add event listener for input changes
                    searchText.addEventListener('input', toggleIconVisibility);

                    // Optional: Clear input when icon is clicked
                    inputIconClose.addEventListener('click', () => {
                        searchText.value = '';
                        toggleIconVisibility();
                        searchText.focus(); // Optional: Refocus the input
                    });
                });
