    <script type="text/javascript">
        var loginForm = $('#loginForm');
        var registerForm = $('#registerForm');
        var apiUrl = "https://gate.ivivu.com/";

        loginForm.submit(function(e) {

            e.preventDefault();

            var emailOrPhone = loginForm.find('input[name="EmailPhoneDN"]').val();
            var pass = loginForm.find('input[name="PasswordDN"]').val();
            // Có những page đăng nhập chuyển tới Member
            var isRedirectMember = loginForm.find('input[name="IsRedirectMember"]').val();

            const apiData = {
                EmailOrPhone: emailOrPhone,
                Password: pass
            };

            $.ajax({
                type: 'POST',
                url: apiUrl + 'api/Account/Login',
                data: JSON.stringify(apiData),
                processData: false,
                contentType: "application/json; charset=utf-8",
                success: function(data) {

                    var resToken = data['auth_token'];
                    document.cookie = "auth_token=" + resToken + ";path=/;domain=" + getCookieDomain();
                    watchToken();

                    if (isRedirectMember === "true") {
                        window.location.href = "https://member.ivivu.com/dashboard/rewards";
                    } else {
                        getUserHeader(); // Load lại Header
                        location.reload();
                        $('#LoginModal').modal("hide");
                    }
                },
                error: function(data) {
                    if (data.responseJSON === undefined)
                        loginForm.find('.errorMsg').text("Không thể kết nối đến máy chủ !");
                    if (data.responseJSON['msg'] !== undefined)
                        loginForm.find('.errorMsg').text(data.responseJSON['msg']);
                },
            });
        });

        registerForm.submit(function(e) {

            e.preventDefault();

            var checkboxPolicy = registerForm.find('input[name="policy-checkbox"]').prop('checked');
            if (!checkboxPolicy) {
                registerForm.find('.errorMsg').text('Bạn phải đồng ý với điều khoản của iVIVU !');
            } else {
                var email = registerForm.find('input[name="EmailDK"]').val();
                var pass = registerForm.find('input[name="PasswordDK"]').val();
                var pass2 = registerForm.find('input[name="RePasswordDK"]').val();

                const apiData = {
                    Email: email,
                    Password: pass,
                    Password2: pass2
                };

                $.ajax({
                    type: 'POST',
                    url: apiUrl + 'api/Account/Register',
                    data: JSON.stringify(apiData),
                    processData: false,
                    contentType: "application/json; charset=utf-8",
                    success: function(data) {
                        var resToken = data['auth_token'];
                        document.cookie = "auth_token=" + resToken + ";path=/;domain=" + getCookieDomain();
                        watchToken();
                        getUserHeader(); // Load lại Header
                        $('#RegisterModal').modal("hide");
                        $('#LoginModal').modal("show");
                    },
                    error: function(data) {
                        if (data.responseJSON === undefined)
                            loginForm.find('.errorMsg').text("Không thể kết nối đến máy chủ !");
                        if (data.responseJSON['msg'] !== undefined)
                            registerForm.find('.errorMsg').text(data.responseJSON['msg']);
                    },
                });
            }

        });

        function getCookie(cname) {
            var name = cname + "=";
            var decodedCookie = decodeURIComponent(document.cookie);
            var ca = decodedCookie.split(';');
            for (var i = 0; i < ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0) == ' ') {
                    c = c.substring(1);
                }
                if (c.indexOf(name) == 0) {
                    return c.substring(name.length, c.length);
                }
            }
            return "";
        }

        function getCookieDomain() {
            if (document.location.hostname == "localhost")
                return document.location.hostname;
            else
                return "ivivu.com";
        }

        function logoutUser() {

            var token = getCookie('auth_token');

            $.ajax({
                type: 'GET',
                url: apiUrl + 'api/Account/Logout',
                contentType: "application/json; charset=utf-8",
                beforeSend: function(xhr) {
                    xhr.setRequestHeader('Authorization', 'Bearer ' + token);
                },
                success: function(data) {
                    //document.cookie = "auth_token=;path=/;domain=" + getCookieDomain();
                    createCookie('auth_token', '', -1, getCookieDomain());
                    watchToken();
                    getUserHeader();
                    location.reload();
                },
                error: function(data) {
                    //document.cookie = "auth_token=;path=/;domain=" + getCookieDomain();
                    createCookie('auth_token', '', -1, getCookieDomain());
                    watchToken();
                    getUserHeader();
                    location.reload();
                },
            });
        }

        var showRegisterDialog = function() {
            registerForm.find('.errorMsg').text('');
            registerForm.find('input[name="EmailDK"]').val('');
            registerForm.find('input[name="PasswordDK"]').val('');
            registerForm.find('input[name="RePasswordDK"]').val('');
            registerForm.find('input[name="policy-checkbox"]').prop("checked", false);
            $("#RegisterModal").modal();
        }
        var showLoginDialog = function() {
            loginForm.find('.errorMsg').text('');
            loginForm.find('input[name="EmailPhoneDN"]').val('');
            loginForm.find('input[name="PasswordDN"]').val('');
            $("#LoginModal").modal();
        }

        function refreshToken(callback) {
            var token = getCookie('auth_token');
            if (!token) return;
            $.ajax({
                type: 'POST',
                url: apiUrl + 'api/account/refreshtoken',
                data: JSON.stringify({
                    currentTokenId: token
                }),
                processData: false,
                contentType: "application/json; charset=utf-8",
                success: function(data) {
                    callback(data);
                },
                error: function(xhr, textStatus, errorThrown) {
                    logoutUser();
                    //if (xhr.status != 401) {
                    //	logoutUser()
                    //}
                },
            });
        }


        function getUserHeader() {
            var headerLogin;

            setTimeout(function() {
                if ($("#hdhdhdhhdhdhdhdhdhd").val() !== '') {

                } else {
                    var token = getCookie('auth_token');
                    var retry = 1;
                    if (token !== null && token !== "") {
                        $.ajax({
                            type: 'GET',
                            url: apiUrl + 'api/Dashboard/Getuserinfo',
                            contentType: "application/json; charset=utf-8",
                            beforeSend: function(xhr) {
                                xhr.setRequestHeader('Authorization', 'Bearer ' + token);
                            },
                            success: function(data) {
                                headerLogin = $("#UserLogged").html();
                                $("#UserLogin .user-menu-list").html(headerLogin);

                                // Get tên User
                                $("#UserLogin .username-header").html(getShortName(data.fullname));
                                // Get Avatar
                                var avatarUrl = data.avatar;
                                $("#UserLogin img.img-circle").attr("src", avatarUrl);
                                // Get email User
                                $("#UserLogin .userMail-header").text(data.email);
                                // Get point User
                                $("#UserLogin .userPoint-header").text(numberStr(data.point));
                            },
                            error: function(xhr, textStatus, errorThrown) {
                                headerLogin = $("#UserNotLogged").html();
                                $("#UserLogin .user-menu-list").html(headerLogin);
                                $("#UserLogin .username-header").html("Tài khoản");
                                $("#UserLogin img.img-circle").attr("src", $("#UserLogin img.img-circle").data("src"));
                                if (xhr.status == 401) {
                                    var self = this;
                                    refreshToken(function(result) {
                                        if (result && result.auth_token) {
                                            createCookie('auth_token', '', -1, getCookieDomain());
                                            token = result.auth_token;
                                            //document.cookie = "auth_token=" + resToken + ";path=/;domain=" + getCookieDomain();
                                            createCookie('auth_token', token, 60, getCookieDomain());
                                            //try again
                                            if (retry > 0) {
                                                retry--;
                                                $.ajax(self);
                                            }
                                        } else {}
                                    });
                                }
                            },

                        });
                    } else {
                        headerLogin = $("#UserNotLogged").html();

                        $("#UserLogin .user-menu-list").html(headerLogin);
                        $("#UserLogin .username-header").html("Tài khoản");
                        $("#UserLogin img.img-circle").attr("src", $("#UserLogin img.img-circle").data("src"));
                    }
                }
            }, 1000)
        }


        // Hiện thông tin user trên Header
        getUserHeader();

        function create_UUID() {
            var dt = new Date().getTime();
            var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
                var r = (dt + Math.random() * 16) % 16 | 0;
                dt = Math.floor(dt / 16);
                return (c === 'x' ? r : (r & 0x3 | 0x8)).toString(16);
            });
            return uuid;
        }

        function watchToken() {
            document.getElementById('token_change').value = create_UUID();
            document.body.click();
        }
        watchToken();

        function numberStr(number) {
            if (number === undefined)
                return 'undefined';
            var parts = number.toString().split(".");
            parts[0] = parts[0].replace(/\B(?=(\d{3})+(?!\d))/g, ",");
            return parts.join(".");
        }

        function checkAvatarURL(avatarUrl) {
            // Nếu là link avatar Google, Facebook
            if (avatarUrl != null) {
                if (avatarUrl.startsWith("http://") || avatarUrl.startsWith("https://"))
                    return avatarUrl;
                else
                    return this.avatarFolderUrl + "/" + avatarUrl;
            } else
                return avatarUrl;
        }

        function getShortName(fullname) {
            if (fullname !== null) {
                // Nếu tên là email thì hiện chữ trước dấu Acòng
                if (fullname.match(/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$/g))
                    return fullname.split("@")[0];
                // Nếu tên là fullname thì hiện Tên chính
                else
                    return fullname.split(" ").pop().replace(/[&\/\\#,+()$~%.'":*?<>{}]/g, '');
            }
        }
    </script>
