    <script>
        // FACEBOOK LOGIN

        window.fbAsyncInit = function() {
            FB.init({
                appId: '628025334278848',
                autoLogAppEvents: true,
                xfbml: true,
                version: 'v10.0'
            });
        };

        (function(d, s, id) {
            var js, fjs = d.getElementsByTagName(s)[0];
            if (d.getElementById(id)) {
                return;
            }
            js = d.createElement(s);
            js.id = id;
            js.src = "https://connect.facebook.net/en_US/sdk.js";
            fjs.parentNode.insertBefore(js, fjs);
        }(document, 'script', 'facebook-jssdk'));

        var socialUser = {
            userData: {
                email: "",
                fullname: "",
                id: "",
                image: "",
                provider: "",
                token: "",
                idToken: ""
            }
        };

        function loginByFacebook() {

            socialUser.userData.provider = "facebook";

            FB.login(function(response) {
                FB.getLoginStatus(function(response) {
                    if (response.status === 'connected') { // Xác thực đã login

                        socialUser.userData.token = response.authResponse.accessToken;

                        FB.api(
                            '/me',
                            'GET', {
                                "fields": "id, name, email"
                            },
                            function(response) {

                                socialUser.userData.fullname = response.name;
                                socialUser.userData.id = response.id;
                                socialUser.userData.image = 'https://graph.facebook.com/' + response.id + '/picture?type=large';

                                // Acc ko có email thì hiện popup nhập
                                if (response.email == "" || response.email == null) {
                                    $('.close-modal-button').click();
                                    $("#GetSocialModal").modal()
                                } else {
                                    socialUser.userData.email = response.email;
                                    loginSocial(socialUser);
                                }
                            }
                        );
                    } else {
                        loginForm.find('.errorMsg').text('Login tài khoản Facebook không thành công !');
                    }
                });
            });
        }

        // GOOGLE LOGIN

        var googleUser = {};
        var startApp = function() {
            gapi.load('auth2', function() {
                auth2 = gapi.auth2.init({
                    //test
                    //client_id: '529716438336-jqqg3n24jtivcuvlslo90o1lc102l1af.apps.googleusercontent.com',
                    //live
                    client_id: '375816142694-o1h018pafnfnb7uacd1uvekddg29m10i.apps.googleusercontent.com',
                });
                attachSignin(document.getElementById('loginGoogleButton'));
                attachSignin(document.getElementById('registerGoogleButton'));
            });
        };

        function attachSignin(element) {
            auth2.attachClickHandler(element, {},
                function(googleUser) {

                    var profile = googleUser.getBasicProfile();
                    socialUser.userData.provider = "google";
                    socialUser.userData.email = profile.getEmail();
                    socialUser.userData.fullname = profile.getName();
                    socialUser.userData.image = profile.getImageUrl();
                    socialUser.userData.idToken = googleUser.getAuthResponse().id_token;

                    loginSocial(socialUser);

                },
                function(error) {
                    loginForm.find('.errorMsg').text('Login tài khoản Google không thành công !');
                });
        }

        startApp();

        // BACKEND Social Login

        function getSocialEmail() {
            if (document.getElementById("socialEmail") != undefined) {
                socialUser.userData.email = document.getElementById("socialEmail").value;
                loginSocial(socialUser);
            }
        }

        function loginSocial(user) {
            $.ajax({
                type: 'POST',
                url: apiUrl + '/api/account/socialLogin',
                data: JSON.stringify(user),
                processData: false,
                contentType: "application/json; charset=utf-8",
                success: function(data) {

                    var resToken = data['auth_token'];
                    document.cookie = "auth_token=" + resToken + ";path=/;domain=" + getCookieDomain();

                    getUserHeader(); // Load lại Header
                    location.reload();
                    $('#LoginModal').modal("hide");
                    $('#RegisterModal').modal("hide");
                },
                error: function(data) {
                    if (data.responseJSON['msg'] !== undefined) {
                        loginForm.find('.errorMsg').text(data.responseJSON['msg']);
                        registerForm.find('.errorMsg').text(data.responseJSON['msg']);
                    }
                },
            });
        }

        function afterUserLogged(successFuc, failFunc) {
            try {
                //const userInfo = parseJwt(getCookie('auth_token'));
                var token = getCookie('auth_token');
                if (token === null || token === "") {
                    if (failFunc) {
                        failFunc();
                    }
                    return false;
                }
                $.ajax({
                    type: 'GET',
                    url: apiUrl + 'api/Dashboard/Getuserinfo',
                    contentType: "application/json; charset=utf-8",
                    beforeSend: function(xhr) {
                        xhr.setRequestHeader('Authorization', 'Bearer ' + token);
                    },
                    success: function(data) {
                        if (data && successFuc) {
                            successFuc(data);
                        } else {
                            if (failFunc) {
                                failFunc();
                            }
                        }
                    },
                    error: function(data) {
                        if (failFunc) {
                            failFunc();
                        }
                        return false;
                    },
                });

            } catch (err) {
                if (failFunc) {
                    failFunc();
                }
                return false;
            }
        }
    </script>
